import React, { useState, useEffect } from 'react';
import { Card, Table, Button, Badge, Modal, Image, Row, Col, Form } from 'react-bootstrap';
import axios from 'axios';
import { getAuthHeader } from '../../services/authService';

const UserVerificationPanel = () => {
  const [loading, setLoading] = useState(true);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showDocumentModal, setShowDocumentModal] = useState(false);
  const [currentDocument, setCurrentDocument] = useState(null);
  const [verificationNotes, setVerificationNotes] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  // Fetch pending verification users
  useEffect(() => {
    const fetchPendingVerifications = async () => {
      try {
        setLoading(true);
        const response = await axios.get('/api/admin/pending-verifications', getAuthHeader());
        setUsers(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load verification data');
        setLoading(false);
        console.error('Error fetching verification data:', err);
      }
    };

    fetchPendingVerifications();
  }, []);

  // View user documents
  const handleViewDocuments = async (userId) => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/admin/user-documents/${userId}`, getAuthHeader());
      setSelectedUser({
        ...users.find(user => user._id === userId),
        documents: response.data.documents
      });
      setLoading(false);
    } catch (err) {
      setError('Failed to load user documents');
      setLoading(false);
      console.error('Error fetching user documents:', err);
    }
  };

  // View a specific document
  const handleViewDocument = (document) => {
    setCurrentDocument(document);
    setShowDocumentModal(true);
  };

  // Approve user verification
  const handleApproveVerification = async (userId) => {
    try {
      await axios.put(`/api/users/${userId}/verify`, { 
        status: 'verified',
        notes: verificationNotes
      }, getAuthHeader());
      
      // Update user in the list
      setUsers(users.map(user => 
        user._id === userId ? { ...user, verificationStatus: 'verified' } : user
      ));
      
      setSuccess('User verification approved successfully');
      setSelectedUser(null);
    } catch (err) {
      setError('Failed to approve verification');
      console.error('Error approving verification:', err);
    }
  };

  // Reject user verification
  const handleRejectVerification = async (userId) => {
    try {
      await axios.put(`/api/users/${userId}/verify`, { 
        status: 'rejected',
        notes: verificationNotes
      }, getAuthHeader());
      
      // Update user in the list
      setUsers(users.map(user => 
        user._id === userId ? { ...user, verificationStatus: 'rejected' } : user
      ));
      
      setSuccess('User verification rejected');
      setSelectedUser(null);
    } catch (err) {
      setError('Failed to reject verification');
      console.error('Error rejecting verification:', err);
    }
  };

  return (
    <Card className="mb-4">
      <Card.Header>
        <h4>User Verification</h4>
      </Card.Header>
      <Card.Body>
        {loading ? (
          <p className="text-center">Loading verification data...</p>
        ) : selectedUser ? (
          <div>
            <h5>Reviewing: {selectedUser.name}</h5>
            <p><strong>Email:</strong> {selectedUser.email}</p>
            <p><strong>Phone:</strong> {selectedUser.phone}</p>
            <p><strong>Address:</strong> {selectedUser.address}</p>
            <p><strong>Annual Income:</strong> ${selectedUser.annualIncome}</p>
            <p><strong>Employment:</strong> {selectedUser.employmentType}, {selectedUser.employmentYears} years</p>
            
            <h6 className="mt-4">Documents:</h6>
            <Row>
              {selectedUser.documents && selectedUser.documents.map(doc => (
                <Col md={4} key={doc.name} className="mb-3">
                  <Card>
                    <Card.Body>
                      <h6>{doc.name}</h6>
                      <Button 
                        variant="primary" 
                        size="sm" 
                        onClick={() => handleViewDocument(doc)}
                        className="w-100"
                      >
                        View Document
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </Row>

            <Form.Group className="mt-3 mb-3">
              <Form.Label>Verification Notes:</Form.Label>
              <Form.Control 
                as="textarea" 
                rows={3} 
                value={verificationNotes}
                onChange={(e) => setVerificationNotes(e.target.value)}
                placeholder="Add notes about this verification (optional)"
              />
            </Form.Group>

            <div className="d-flex justify-content-between mt-4">
              <Button 
                variant="secondary" 
                onClick={() => {
                  setSelectedUser(null);
                  setVerificationNotes('');
                }}
              >
                Back to List
              </Button>
              <div>
                <Button 
                  variant="danger" 
                  onClick={() => handleRejectVerification(selectedUser._id)}
                  className="me-2"
                >
                  Reject Verification
                </Button>
                <Button 
                  variant="success" 
                  onClick={() => handleApproveVerification(selectedUser._id)}
                >
                  Approve Verification
                </Button>
              </div>
            </div>
          </div>
        ) : (
          <>
            {error && <div className="alert alert-danger">{error}</div>}
            {success && <div className="alert alert-success">{success}</div>}
            
            {users.length === 0 ? (
              <p className="text-center">No users pending verification.</p>
            ) : (
              <Table responsive striped hover>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Status</th>
                    <th>Loan Applications</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map(user => (
                    <tr key={user._id}>
                      <td>{user.name}</td>
                      <td>{user.email}</td>
                      <td>
                        <Badge bg={
                          user.verificationStatus === 'pending' ? 'warning' : 
                          user.verificationStatus === 'verified' ? 'success' : 'danger'
                        }>
                          {user.verificationStatus}
                        </Badge>
                      </td>
                      <td>{user.loanCount}</td>
                      <td>
                        <Button 
                          variant="primary" 
                          size="sm" 
                          onClick={() => handleViewDocuments(user._id)}
                        >
                          Review Documents
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </>
        )}
      </Card.Body>

      {/* Document Viewer Modal */}
      <Modal 
        show={showDocumentModal} 
        onHide={() => setShowDocumentModal(false)}
        size="lg"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>
            {currentDocument?.name}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className="text-center">
          {currentDocument && (
            <Image 
              src={`/api/documents/${currentDocument.filename}`} 
              alt={currentDocument.name}
              className="img-fluid"
              style={{ maxHeight: '70vh' }}
            />
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDocumentModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Card>
  );
};

export default UserVerificationPanel;
