import React, { useState, useEffect } from 'react';
import { Card, Table, Button, Badge, Modal, Image, Row, Col, Form, Alert } from 'react-bootstrap';
import { getPendingVerifications, getUserDocuments, updateUserVerification } from '../../services/adminService';

const UserVerificationPanel = () => {
  const [loading, setLoading] = useState(true);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showDocumentModal, setShowDocumentModal] = useState(false);
  const [currentDocument, setCurrentDocument] = useState(null);
  const [verificationNotes, setVerificationNotes] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [pendingUsers, setPendingUsers] = useState([]);
  const [documents, setDocuments] = useState([]);

  useEffect(() => {
    fetchPendingVerifications();
  }, []);

  const fetchPendingVerifications = async () => {
    try {
      setLoading(true);
      const data = await getPendingVerifications();
      setPendingUsers(data);
    } catch (err) {
      setError('Failed to fetch pending verifications');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // View user documents
  const handleViewDocuments = async (userId) => {
    try {
      const docs = await getUserDocuments(userId);
      setDocuments(docs);
      setSelectedUser(pendingUsers.find(user => user._id === userId));
      setShowDocumentModal(true);
    } catch (err) {
      setError('Failed to fetch user documents');
      console.error(err);
    }
  };

  // View a specific document
  const handleViewDocument = (document) => {
    setCurrentDocument(document);
    setShowDocumentModal(true);
  };

  const handleVerification = async (status) => {
    if (!selectedUser) return;

    try {
      await updateUserVerification(selectedUser._id, status, verificationNotes);
      setShowDocumentModal(false);
      setVerificationNotes('');
      fetchPendingVerifications();
    } catch (err) {
      setError('Failed to update verification status');
      console.error(err);
    }
  };

  return (
    <Card className="mb-4">
      <Card.Header>
        <h4>User Verification</h4>
      </Card.Header>
      <Card.Body>
        {error && <Alert variant="danger">{error}</Alert>}
        
        {loading ? (
          <p className="text-center">Loading verification data...</p>
        ) : selectedUser ? (
          <div>
            <h5>Reviewing: {selectedUser.name}</h5>
            <p><strong>Email:</strong> {selectedUser.email}</p>
            <p><strong>Phone:</strong> {selectedUser.phone}</p>
            <p><strong>Address:</strong> {selectedUser.address}</p>
            <p><strong>Annual Income:</strong> ${selectedUser.annualIncome}</p>
            <p><strong>Employment:</strong> {selectedUser.employmentType}, {selectedUser.employmentYears} years</p>
            
            <h6 className="mt-4">Documents:</h6>
            <Row>
              {documents.map((doc, index) => (
                <Col md={4} key={index} className="mb-3">
                  <Card>
                    <Card.Body>
                      <h6>{doc.filename}</h6>
                      <Button 
                        variant="primary" 
                        size="sm" 
                        onClick={() => handleViewDocument(doc)}
                        className="w-100"
                      >
                        View Document
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </Row>

            <Form.Group className="mt-3 mb-3">
              <Form.Label>Verification Notes:</Form.Label>
              <Form.Control 
                as="textarea" 
                rows={3} 
                value={verificationNotes}
                onChange={(e) => setVerificationNotes(e.target.value)}
                placeholder="Add notes about this verification (optional)"
              />
            </Form.Group>

            <div className="d-flex justify-content-between mt-4">
              <Button 
                variant="secondary" 
                onClick={() => {
                  setSelectedUser(null);
                  setVerificationNotes('');
                }}
              >
                Back to List
              </Button>
              <div>
                <Button 
                  variant="danger" 
                  onClick={() => handleVerification('rejected')}
                  className="me-2"
                >
                  Reject Verification
                </Button>
                <Button 
                  variant="success" 
                  onClick={() => handleVerification('verified')}
                >
                  Approve Verification
                </Button>
              </div>
            </div>
          </div>
        ) : (
          <>
            {error && <div className="alert alert-danger">{error}</div>}
            {success && <div className="alert alert-success">{success}</div>}
            
            {pendingUsers.length === 0 ? (
              <p className="text-center">No users pending verification.</p>
            ) : (
              <Table responsive striped hover>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Status</th>
                    <th>Loan Applications</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {pendingUsers.map(user => (
                    <tr key={user._id}>
                      <td>{user.name}</td>
                      <td>{user.email}</td>
                      <td>
                        <Badge bg={
                          user.verificationStatus === 'pending' ? 'warning' : 
                          user.verificationStatus === 'verified' ? 'success' : 'danger'
                        }>
                          {user.verificationStatus}
                        </Badge>
                      </td>
                      <td>{user.loanCount}</td>
                      <td>
                        <Button 
                          variant="primary" 
                          size="sm" 
                          onClick={() => handleViewDocuments(user._id)}
                        >
                          Review Documents
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </>
        )}
      </Card.Body>

      {/* Document Viewer Modal */}
      <Modal 
        show={showDocumentModal} 
        onHide={() => setShowDocumentModal(false)}
        size="lg"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>
            {currentDocument?.filename}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className="text-center">
          {currentDocument && (
            <Image 
              src={`http://localhost:5000/api/documents/${currentDocument.filename}`} 
              alt={currentDocument.filename}
              className="img-fluid"
              style={{ maxHeight: '70vh' }}
            />
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDocumentModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Card>
  );
};

export default UserVerificationPanel;
