import React, { useState, useEffect } from 'react';
import { Card, Table, Button, Badge, Modal, Image, Row, Col, Form, Alert } from 'react-bootstrap';
import { getPendingVerifications, getUserDocuments, updateUserVerification } from '../../services/adminService';

const UserVerificationPanel = () => {
  const [loading, setLoading] = useState(true);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showDocumentModal, setShowDocumentModal] = useState(false);
  const [currentDocument, setCurrentDocument] = useState(null);
  const [verificationNotes, setVerificationNotes] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [pendingUsers, setPendingUsers] = useState([]);
  const [documents, setDocuments] = useState({});

  useEffect(() => {
    console.log('UserVerificationPanel mounted, fetching data...');
    fetchPendingVerifications();
  }, []);

  const fetchPendingVerifications = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('Calling getPendingVerifications...');
      const data = await getPendingVerifications();
      console.log('Received pending verifications:', data);
      setPendingUsers(data || []);
    } catch (err) {
      console.error('Error fetching verifications:', err);
      setError('Failed to fetch pending verifications: ' + (err.message || JSON.stringify(err)));
    } finally {
      setLoading(false);
    }
  };

  // View user documents
  const handleViewDocuments = async (userId) => {
    try {
      setError(null);
      console.log('Fetching documents for user:', userId);
      const docs = await getUserDocuments(userId);
      console.log('Received documents:', docs);
      setDocuments(docs);
      setSelectedUser(pendingUsers.find(user => user._id === userId));
      
      // Check if user has no documents
      const user = pendingUsers.find(u => u._id === userId);
      if (user && user.verificationStatus === 'not_submitted') {
        setError('This user has not submitted verification documents yet but has loan applications. They need to upload documents before verification.');
      }
    } catch (err) {
      console.error('Error fetching documents:', err);
      setError('Failed to fetch user documents: ' + (err.message || JSON.stringify(err)));
    }
  };

  // View a specific document
  const handleViewDocument = (docType, filename) => {
    if (!filename) return;
    
    setCurrentDocument({
      type: docType,
      filename: filename,
      userId: selectedUser._id
    });
    setShowDocumentModal(true);
  };

  const handleVerification = async (status) => {
    if (!selectedUser) return;

    try {
      setError(null);
      await updateUserVerification(selectedUser._id, status, verificationNotes);
      setSuccess(`User ${selectedUser.name} has been ${status === 'verified' ? 'approved' : 'rejected'}`);
      setSelectedUser(null);
      setVerificationNotes('');
      fetchPendingVerifications();
    } catch (err) {
      setError('Failed to update verification status');
      console.error(err);
    }
  };

  return (
    <Card className="mb-4">
      <Card.Header>
        <h4>User Verification</h4>
      </Card.Header>
      <Card.Body>
        {error && <Alert variant="danger">{error}</Alert>}
        {success && <Alert variant="success">{success}</Alert>}
        
        {loading ? (
          <p className="text-center">Loading verification data...</p>
        ) : selectedUser ? (
          <div>
            <h5>Reviewing: {selectedUser.name}</h5>
            <p><strong>Email:</strong> {selectedUser.email}</p>
            <p><strong>Phone:</strong> {selectedUser.phone}</p>
            
            <h6 className="mt-4">Documents:</h6>
            <Row>
              {documents.aadharCard && (
                <Col md={4} className="mb-3">
                  <Card>
                    <Card.Body>
                      <h6>Aadhar Card</h6>
                      <Button 
                        variant="primary" 
                        size="sm" 
                        onClick={() => handleViewDocument('aadharCard', documents.aadharCard)}
                        className="w-100"
                      >
                        View Document
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              )}
              
              {documents.panCard && (
                <Col md={4} className="mb-3">
                  <Card>
                    <Card.Body>
                      <h6>PAN Card</h6>
                      <Button 
                        variant="primary" 
                        size="sm" 
                        onClick={() => handleViewDocument('panCard', documents.panCard)}
                        className="w-100"
                      >
                        View Document
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              )}
              
              {documents.incomeProof && (
                <Col md={4} className="mb-3">
                  <Card>
                    <Card.Body>
                      <h6>Income Proof</h6>
                      <Button 
                        variant="primary" 
                        size="sm" 
                        onClick={() => handleViewDocument('incomeProof', documents.incomeProof)}
                        className="w-100"
                      >
                        View Document
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              )}
            </Row>

            <Form.Group className="mt-3 mb-3">
              <Form.Label>Verification Notes:</Form.Label>
              <Form.Control 
                as="textarea" 
                rows={3} 
                value={verificationNotes}
                onChange={(e) => setVerificationNotes(e.target.value)}
                placeholder="Add notes about this verification (optional)"
              />
            </Form.Group>

            <div className="d-flex justify-content-between mt-4">
              <Button 
                variant="secondary" 
                onClick={() => {
                  setSelectedUser(null);
                  setVerificationNotes('');
                }}
              >
                Back to List
              </Button>
              <div>
                <Button 
                  variant="danger" 
                  onClick={() => handleVerification('rejected')}
                  className="me-2"
                >
                  Reject Verification
                </Button>
                <Button 
                  variant="success" 
                  onClick={() => handleVerification('verified')}
                >
                  Approve Verification
                </Button>
              </div>
            </div>
          </div>
        ) : (
          <>
            {pendingUsers.length === 0 ? (
              <div className="text-center">
                <p>No users pending verification.</p>
                <Button 
                  variant="primary" 
                  onClick={fetchPendingVerifications}
                  className="mt-2"
                >
                  Refresh Verifications
                </Button>
              </div>
            ) : (
              <>
                <div className="mb-3">
                  <Button variant="outline-primary" size="sm" onClick={fetchPendingVerifications}>
                    Refresh Data
                  </Button>
                  <span className="ms-2">Found {pendingUsers.length} users pending verification</span>
                </div>
                <Table responsive striped hover>
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Status</th>
                      <th>Loan Applications</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {pendingUsers.map(user => (
                      <tr key={user._id}>
                        <td>{user.name}</td>
                        <td>{user.email}</td>
                        <td>
                          <Badge bg={
                            user.verificationStatus === 'pending' ? 'warning' : 
                            user.verificationStatus === 'verified' ? 'success' : 
                            user.verificationStatus === 'not_submitted' ? 'secondary' : 'danger'
                          }>
                            {user.verificationStatus === 'not_submitted' ? 'Not Submitted' : user.verificationStatus}
                          </Badge>
                        </td>
                        <td>{user.loanCount}</td>
                        <td>
                          <Button 
                            variant="primary" 
                            size="sm" 
                            onClick={() => handleViewDocuments(user._id)}
                          >
                            Review Documents
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </>
            )}
          </>
        )}
      </Card.Body>

      {/* Document Viewer Modal */}
      <Modal 
        show={showDocumentModal} 
        onHide={() => setShowDocumentModal(false)}
        size="lg"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>
            {currentDocument?.type === 'aadharCard' && 'Aadhar Card'}
            {currentDocument?.type === 'panCard' && 'PAN Card'}
            {currentDocument?.type === 'incomeProof' && 'Income Proof'}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className="text-center">
          {currentDocument && (
            <div className="document-preview">
              <p className="mb-3">Filename: {currentDocument.filename}</p>
              <div className="border p-3">
                <p className="text-muted">Document preview would appear here in a real application</p>
                <p>This is a placeholder for document: {currentDocument.filename}</p>
              </div>
            </div>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDocumentModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Card>
  );
};

export default UserVerificationPanel;
