import React, { useState } from 'react';
import { Container, Row, Col, Card, Form, Button, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { applyForLoan } from '../services/loanService';

const LoanApplication = () => {
  const [formData, setFormData] = useState({
    amount: '',
    purpose: '',
    term: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const navigate = useNavigate();
  const { amount, purpose, term } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setIsLoading(true);

    // Basic validation
    if (!amount || !purpose || !term) {
      setError('Please fill in all required fields');
      setIsLoading(false);
      return;
    }

    try {
      const loanData = {
        amount: parseFloat(amount),
        purpose,
        term: parseInt(term)
      };

      await applyForLoan(loanData);
      setSuccess('Loan application submitted successfully!');
      
      // Redirect to dashboard after 2 seconds
      setTimeout(() => {
        navigate('/dashboard');
      }, 2000);
    } catch (err) {
      setError(err.message || 'Failed to submit loan application. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Container>
      <Row className="justify-content-center mt-4">
        <Col md={8}>
          <Card>
            <Card.Header>
              <h3 className="mb-0">Loan Application</h3>
            </Card.Header>
            <Card.Body>
              {error && <Alert variant="danger">{error}</Alert>}
              {success && <Alert variant="success">{success}</Alert>}

              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Loan Amount ($)</Form.Label>
                  <Form.Control
                    type="number"
                    name="amount"
                    value={amount}
                    onChange={handleChange}
                    placeholder="Enter loan amount"
                    min="100"
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Purpose of Loan</Form.Label>
                  <Form.Control
                    as="select"
                    name="purpose"
                    value={purpose}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select purpose</option>
                    <option value="Home Improvement">Home Improvement</option>
                    <option value="Debt Consolidation">Debt Consolidation</option>
                    <option value="Education">Education</option>
                    <option value="Medical Expenses">Medical Expenses</option>
                    <option value="Business">Business</option>
                    <option value="Vehicle Purchase">Vehicle Purchase</option>
                    <option value="Other">Other</option>
                  </Form.Control>
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Loan Term (months)</Form.Label>
                  <Form.Control
                    as="select"
                    name="term"
                    value={term}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select term</option>
                    <option value="6">6 months</option>
                    <option value="12">12 months</option>
                    <option value="24">24 months</option>
                    <option value="36">36 months</option>
                    <option value="48">48 months</option>
                    <option value="60">60 months</option>
                  </Form.Control>
                </Form.Group>

                <Button
                  variant="primary"
                  type="submit"
                  className="w-100 mt-3"
                  disabled={isLoading}
                >
                  {isLoading ? 'Submitting...' : 'Submit Application'}
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default LoanApplication;
