import axios from 'axios';

// Get user's EMIs
export const getUserEmis = async () => {
  try {
    const res = await axios.get('/api/emis/me');
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Get loan EMIs
export const getLoanEmis = async (loanId) => {
  try {
    const res = await axios.get(`/api/emis/loan/${loanId}`);
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Pay EMI
export const payEmi = async (emiId, paymentId) => {
  try {
    const res = await axios.put(`/api/emis/${emiId}/pay`, { paymentId });
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Get all EMIs (admin only)
export const getAllEmis = async () => {
  try {
    const res = await axios.get('/api/emis');
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Update overdue EMIs (admin only)
export const updateOverdueEmis = async () => {
  try {
    const res = await axios.get('/api/emis/update-overdue');
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Manually update EMI payment status (admin only)
export const manualUpdateEmi = async (emiId, updateData) => {
  try {
    const res = await axios.put(`/api/emis/${emiId}/manual-update`, updateData);
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};
