import React, { useState, useContext, useEffect } from 'react';
import { Container, Form, Button, Alert, Card, Row, Col, ProgressBar } from 'react-bootstrap';
import { Navigate, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { submitKYC } from '../services/userService';

const KycSubmission = () => {
  const { user, setUser } = useContext(AuthContext);
  const navigate = useNavigate();
  
  const [aadharCard, setAadharCard] = useState(null);
  const [panCard, setPanCard] = useState(null);
  const [incomeProof, setIncomeProof] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  
  // Use effect to handle redirects based on authentication and verification status
  useEffect(() => {
    // Redirect if not logged in
    if (!user) {
      navigate('/login');
      return;
    }
    
    // Redirect if already verified or pending verification
    if (user.verificationStatus === 'verified' || user.verificationStatus === 'pending') {
      navigate('/dashboard');
    }
  }, [user, navigate]);
  
  const handleFileChange = (e, setFile) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        setError('File size should not exceed 5MB');
        return;
      }
      setFile(file);
    }
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!aadharCard || !panCard || !incomeProof) {
      setError('Please upload all required documents');
      return;
    }
    
    setLoading(true);
    setError('');
    
    try {
      const result = await submitKYC(aadharCard, panCard, incomeProof);
      setSuccess('Documents submitted successfully. Your KYC verification is pending review.');
      
      // Update the user context with new verification status
      setUser({
        ...user,
        verificationStatus: 'pending'
      });
      
      // Redirect to dashboard after 3 seconds
      setTimeout(() => {
        navigate('/dashboard');
      }, 3000);
      
    } catch (err) {
      setError(err.toString());
    } finally {
      setLoading(false);
    }
  };
  
  // If auth context is still loading or no user exists, don't render the form yet
  if (!user) {
    return null;
  }
  
  return (
    <Container className="py-5">
      <Card>
        <Card.Header as="h4" className="text-center bg-primary text-white">
          KYC Verification
        </Card.Header>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          {success && <Alert variant="success">{success}</Alert>}
          
          <Card.Text className="mb-4">
            To continue with our services, we need to verify your identity. Please upload the following documents:
          </Card.Text>
          
          <Form onSubmit={handleSubmit}>
            <Row className="mb-4">
              <Col md={4}>
                <Card className={`h-100 ${aadharCard ? 'border-success' : 'border-light'}`}>
                  <Card.Body>
                    <Card.Title>Aadhar Card</Card.Title>
                    <Card.Text className="text-muted small">
                      Upload front and back side in a single PDF or image file. Max size 5MB.
                    </Card.Text>
                    
                    <Form.Group controlId="aadharCard" className="mt-3">
                      <Form.Control 
                        type="file" 
                        onChange={(e) => handleFileChange(e, setAadharCard)}
                        accept=".jpg,.jpeg,.png,.pdf"
                      />
                      {aadharCard && (
                        <div className="mt-2 text-success small">
                          <i className="fas fa-check-circle"></i> {aadharCard.name}
                        </div>
                      )}
                    </Form.Group>
                  </Card.Body>
                </Card>
              </Col>
              
              <Col md={4}>
                <Card className={`h-100 ${panCard ? 'border-success' : 'border-light'}`}>
                  <Card.Body>
                    <Card.Title>PAN Card</Card.Title>
                    <Card.Text className="text-muted small">
                      Upload a clear scan or photo of your PAN card. Max size 5MB.
                    </Card.Text>
                    
                    <Form.Group controlId="panCard" className="mt-3">
                      <Form.Control 
                        type="file" 
                        onChange={(e) => handleFileChange(e, setPanCard)}
                        accept=".jpg,.jpeg,.png,.pdf"
                      />
                      {panCard && (
                        <div className="mt-2 text-success small">
                          <i className="fas fa-check-circle"></i> {panCard.name}
                        </div>
                      )}
                    </Form.Group>
                  </Card.Body>
                </Card>
              </Col>
              
              <Col md={4}>
                <Card className={`h-100 ${incomeProof ? 'border-success' : 'border-light'}`}>
                  <Card.Body>
                    <Card.Title>Income Proof</Card.Title>
                    <Card.Text className="text-muted small">
                      Upload salary slips, tax returns, or bank statements. Max size 5MB.
                    </Card.Text>
                    
                    <Form.Group controlId="incomeProof" className="mt-3">
                      <Form.Control 
                        type="file" 
                        onChange={(e) => handleFileChange(e, setIncomeProof)}
                        accept=".jpg,.jpeg,.png,.pdf"
                      />
                      {incomeProof && (
                        <div className="mt-2 text-success small">
                          <i className="fas fa-check-circle"></i> {incomeProof.name}
                        </div>
                      )}
                    </Form.Group>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
            
            <div className="text-center mt-4">
              <Button 
                type="submit" 
                variant="primary" 
                size="lg"
                disabled={loading || !aadharCard || !panCard || !incomeProof}
                className="px-5"
              >
                {loading ? 'Uploading...' : 'Submit Documents'}
              </Button>
              
              {loading && (
                <ProgressBar animated now={100} className="mt-3" />
              )}
            </div>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default KycSubmission;
