import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Alert } from 'react-bootstrap';
import { getAdminStats } from '../../services/adminService';

const AdminStats = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      setLoading(true);
      const data = await getAdminStats();
      setStats(data);
    } catch (err) {
      setError('Failed to fetch admin statistics');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p className="text-center">Loading statistics...</p>;
  if (error) return <Alert variant="danger">{error}</Alert>;
  if (!stats) return null;

  return (
    <Row>
      <Col md={3}>
        <Card className="mb-4">
          <Card.Body>
            <h6 className="text-muted">Pending Verifications</h6>
            <h3>{stats.pendingVerifications}</h3>
          </Card.Body>
        </Card>
      </Col>
      <Col md={3}>
        <Card className="mb-4">
          <Card.Body>
            <h6 className="text-muted">Total Users</h6>
            <h3>{stats.totalUsers}</h3>
          </Card.Body>
        </Card>
      </Col>
      <Col md={3}>
        <Card className="mb-4">
          <Card.Body>
            <h6 className="text-muted">Active Loans</h6>
            <h3>{stats.activeLoans}</h3>
          </Card.Body>
        </Card>
      </Col>
      <Col md={3}>
        <Card className="mb-4">
          <Card.Body>
            <h6 className="text-muted">Overdue EMIs</h6>
            <h3>{stats.overdueEmis}</h3>
          </Card.Body>
        </Card>
      </Col>
    </Row>
  );
};

export default AdminStats;
