import axios from 'axios';

// Apply for a loan
export const applyForLoan = async (loanData) => {
  try {
    const res = await axios.post('/api/loans', loanData);
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Get all loans (admin only)
export const getAllLoans = async () => {
  try {
    const res = await axios.get('/api/loans');
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Get pending loans (admin only)
export const getPendingLoans = async () => {
  try {
    const res = await axios.get('/api/loans/pending');
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Get user's loans
export const getUserLoans = async () => {
  try {
    const res = await axios.get('/api/loans/me');
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Get loan by ID
export const getLoanById = async (id) => {
  try {
    const res = await axios.get(`/api/loans/${id}`);
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Approve loan (admin only)
export const approveLoan = async (id) => {
  try {
    const res = await axios.put(`/api/loans/${id}/approve`);
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Reject loan (admin only)
export const rejectLoan = async (id, rejectionReason) => {
  try {
    const res = await axios.put(`/api/loans/${id}/reject`, { rejectionReason });
    return res.data;
  } catch (error) {
    throw error.response.data;
  }
};
