import React, { useState, useEffect, useContext } from 'react';
import { Container, Row, Col, Card, Badge, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { getUserLoans } from '../services/loanService';

// Helper function to format currency
const formatCurrency = (amount) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
  }).format(amount);
};

// Helper function to format date
const formatDate = (dateString) => {
  const options = { year: 'numeric', month: 'long', day: 'numeric' };
  return new Date(dateString).toLocaleDateString(undefined, options);
};

// Loan status badge component
const LoanStatusBadge = ({ status }) => {
  let variant;
  switch (status) {
    case 'approved':
      variant = 'success';
      break;
    case 'rejected':
      variant = 'danger';
      break;
    case 'pending':
      variant = 'warning';
      break;
    case 'paid':
      variant = 'primary';
      break;
    default:
      variant = 'secondary';
  }
  
  return <Badge bg={variant}>{status.toUpperCase()}</Badge>;
};

const Dashboard = () => {
  const { user } = useContext(AuthContext);
  const [loans, setLoans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchLoans = async () => {
      try {
        const data = await getUserLoans();
        setLoans(data);
      } catch (err) {
        setError('Failed to fetch your loans. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchLoans();
  }, []);

  if (loading) {
    return (
      <Container className="mt-4">
        <Card>
          <Card.Body className="text-center">
            <p>Loading your loans...</p>
          </Card.Body>
        </Card>
      </Container>
    );
  }

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h2>Welcome, {user?.name}</h2>
          <p>Manage your loan applications and track their status.</p>
        </Col>
        <Col xs="auto">
          <Button as={Link} to="/apply-loan" variant="primary">
            Apply for New Loan
          </Button>
        </Col>
      </Row>

      {error && <Alert variant="danger">{error}</Alert>}

      <Row>
        <Col>
          <Card>
            <Card.Header>
              <h3>My Loans</h3>
            </Card.Header>
            <Card.Body>
              {loans.length === 0 ? (
                <p className="text-center">You haven't applied for any loans yet.</p>
              ) : (
                loans.map((loan) => (
                  <Card key={loan._id} className="mb-3">
                    <Card.Body>
                      <Row>
                        <Col>
                          <h5>{loan.purpose}</h5>
                          <p className="mb-1">
                            <strong>Amount:</strong> {formatCurrency(loan.amount)}
                          </p>
                          <p className="mb-1">
                            <strong>Term:</strong> {loan.term} months
                          </p>
                          <p className="mb-1">
                            <strong>Applied on:</strong> {formatDate(loan.applicationDate)}
                          </p>
                          {loan.approvalDate && (
                            <p className="mb-1">
                              <strong>Approved on:</strong> {formatDate(loan.approvalDate)}
                            </p>
                          )}
                          {loan.rejectionReason && (
                            <p className="mb-1">
                              <strong>Rejection Reason:</strong> {loan.rejectionReason}
                            </p>
                          )}
                        </Col>
                        <Col xs="auto" className="d-flex flex-column align-items-end">
                          <LoanStatusBadge status={loan.status} />
                          <div className="mt-auto">
                            <Button 
                              as={Link} 
                              to={`/loans/${loan._id}`} 
                              variant="outline-primary" 
                              size="sm"
                              className="mt-2"
                            >
                              View Details
                            </Button>
                          </div>
                        </Col>
                      </Row>
                    </Card.Body>
                  </Card>
                ))
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;
